mapper.select:
SELECT COUNT(*) > 0
FROM diet_plans
WHERE id = #{dietPlanId}

SELECT *
FROM diet_plans
WHERE user_id = #{userId}
  and is_primary = 1;

mapper.update:
UPDATE diet_plans
SET is_primary = FALSE
WHERE user_id = #{userId}
  AND is_primary = TRUE;

UPDATE diet_plans
SET is_primary = TRUE
WHERE user_id = #{userId}
  AND id = #{dietPlanId}
